INSERT INTO `bz_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`) VALUES
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to Quản lý bán hàng.com', 'Connect to get important product notifications and updates.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-06 15:56:30', '0000-00-00 00:00:00', 0, '', '', 0),
(4, 'wc-update-db-reminder', 'update', 'en_US', 'WooCommerce database update done', 'WooCommerce database update complete. Thank you for updating to the latest version!', 'info', '{}', 'unactioned', 'woocommerce-core', '2020-04-01 01:56:30', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(6, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the Quản lý bán hàng mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-05-14 10:14:30', '0000-00-00 00:00:00', 0, '', '', 0),
(7, 'wc-admin-facebook-extension', 'info', 'en_US', 'Market on Facebook', 'Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.', 'thumbs-up', '{}', 'unactioned', 'woocommerce-admin', '2020-05-14 10:14:30', '0000-00-00 00:00:00', 0, '', '', 0),
(8, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Help Quản lý bán hàng improve with usage tracking', 'Gathering usage data allows us to improve Quản lý bán hàng. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"https://dulich.w2steam.com/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-14 10:14:30', '0000-00-00 00:00:00', 0, '', '', 0),
(9, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Tips, product updates, and inspiration', 'Chúng tôi ở đây cùng bạn — nhận các mẹo, thông tin, và cảm hứng trực tiếp đến email của bạn.', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-05-14 10:14:30', '0000-00-00 00:00:00', 0, '', '', 0),
(10, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-10-18 08:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(11, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connect with your audience', 'Grow your customer base and increase your sales with marketing tools built for WooCommerce.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-10-18 08:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(12, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Give feedback', 'Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-10-18 08:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(13, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-10-18 08:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(14, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-10-18 08:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(15, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', 'info', '{}', 'pending', 'woocommerce.com', '2020-10-10 18:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(16, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Shipping & Tax', 'WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', 'info', '{}', 'pending', 'woocommerce.com', '2020-10-10 18:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
(17, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.', 'info', '{}', 'pending', 'woocommerce.com', '2020-10-10 18:53:53', '0000-00-00 00:00:00', 0, 'plain', '', 0),
